mapper.sql:
from
	code b
	left join codegroup a
	on a.ifcgSeq = b.codegroup_ifcgSeq
where 1=1
	AND ifcdDelNy = #{shDelNy} 
	AND ifcdUseNy = #{shUseNy} 



		AND b.registerDate BETWEEN #{shDateStart} AND #{shDateEnd} 
		AND b.reviseDate BETWEEN #{shDateStart} AND #{shDateEnd} 



		AND codegroup_ifcgSeq = #{shValue}
		AND ifcdName LIKE CONCAT('%',#{shValue},'%')

mapper.delete:
DELETE FROM code
WHERE 1=1
	AND ifcdSeq = #{ifcdSeq}
