mapper.sql:

from
    hotel a
left join (select * from hotelroom b order by b.htrRoomPrice limit 1) c on c.hotel_seq = a.htseq
where 1=1
	AND htDelNy = #{shDelNy} 


	AND htStarSearch = #{shStar}
	AND htStarSearch = #{shStar}
	AND htStarSearch = #{shStar}
	AND htStarSearch = #{shStar}
	AND htStarSearch = #{shStar}


	AND htRestaurantNy = #{shRestaurant}


	AND htSpaNy = #{shSpa}


	AND htFitnessCenterNy = #{shFitnessCenter}


	AND htPoolNy = #{shPool}


	AND htloungeNy = #{shlounge}


	AND htStockRoomNy = #{shStockRoom}


	AND htParkingNy = #{shParking}


	AND htShuttleBusNy = #{shShuttleBus}


	AND htBreakfastNy = #{shBreakfast}


	AND htRoomServiceNy = #{shRoomService}


	AND htElseNy = #{shElse}



		AND a.htRegDate BETWEEN #{shDateStart} AND #{shDateEnd} 
		AND a.htReviseDate BETWEEN #{shDateStart} AND #{shDateEnd} 




		AND htseq = #{shValue}
		AND htName LIKE CONCAT('%',#{shValue},'%')
		AND htStarSearch LIKE CONCAT('%',#{shValue},'%')

mapper.delete:
delete from hotel
where 1=1 
	and htseq = #{htseq}
