mapper.sql:
FROM
	newjeans.hotelmember a
WHERE 1=1
	AND htmDelNy = #{shDelNy}
	AND htmRegAgreeContentNy = #{shRegAgreeContent}



		AND a.htmRegDate BETWEEN #{shDateStart} AND #{shDateEnd} 
		AND a.htmEditMemberDate BETWEEN #{shDateStart} AND #{shDateEnd} 




		AND htmSeq = #{shValue}
		AND htmUserName LIKE CONCAT('%',#{shValue},'%')
		AND htmGender LIKE CONCAT('%',#{shValue},'%')
		AND htmGrade LIKE CONCAT('%',#{shValue},'%')
		AND htmEmail LIKE CONCAT('%',#{shValue},'%')
		AND htmPhoneNumber LIKE CONCAT('%',#{shValue},'%')
		AND htmId LIKE CONCAT('%',#{shValue},'%')

mapper.delete:
DELETE FROM hotelmember
WHERE 1=1
	AND htmSeq = #{htmSeq}
