mapper.select:
SELECT a.ifcgSeq, a.ifcgName, a.ifcgUseNy, a.ifcgOrder, a.ifcgDelNy, a.ifcgDesc, a.regDateTime, a.regDateTimeRevise, (select count(ifcdSeq) from Code b where 1=1 and b.codegroup_ifcgSeq = a.ifcgSeq) as count FROM newjeans.codegroup a WHERE 1=1 AND ifcgDelNy = #{shDelNy} AND ifcgUseNy = #{shUseNy}
AND a.regDateTime BETWEEN #{shDateStart} AND #{shDateEnd} AND a.regDateTimeRevise BETWEEN #{shDateStart} AND #{shDateEnd}
AND ifcgSeq = #{shValue} AND ifcgName LIKE CONCAT('%',#{shValue},'%')
ORDER BY a.ifcgSeq desc

mapper.delete:
DELETE FROM codegroup
WHERE 1=1
	AND ifcgSeq = #{ifcgSeq}
